import React, {useState} from 'react';
import axios from "axios";

import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from "react-bootstrap/Form";
import "react-datepicker/dist/react-datepicker.css";

import * as config from "../services/config";

const AddNewAccountDialog = (props) => {

    let [firstName, lastName, emailAddress, dateOfBirth] = useState();

    const handleChangeFistName = e => {
        firstName = e.target.value;
        // setFirstName(e.target.value)
    };

    const handleChangeLastName = e => {
        lastName = e.target.value;
    };

    const handleChangeEmailAddress = e => {
        emailAddress = e.target.value;
    };

    const handleChangeDateOfBirth = e => {
        dateOfBirth = e.target.value;
    };

    const handleSubmit = event => {
        const newAccount = {
            fistName: firstName,
            lastName: lastName,
            emailAddress: emailAddress,
            dateOfBirth: dateOfBirth
        };
        createNewUserAccount(newAccount);
        event.preventDefault()
    };

    const createNewUserAccount = (newUserAccount) => {
        axios.post(config.userAccountManagementBackendUrl, newUserAccount)
            .then(res => {
                const account = res.data;
                console.log(account);
                return account;
            })
    };

    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered>
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Add New User Account
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>

                <Form onSubmit={handleSubmit}>
                    <Form.Label>First Name</Form.Label>
                    <Form.Control type="text" value={firstName} onChange={handleChangeFistName} />
                    <Form.Label>Last Name</Form.Label>
                    <Form.Control type="text" value={lastName} onChange={handleChangeLastName}/>

                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" value={emailAddress} onChange={handleChangeEmailAddress}/>
                    </Form.Group>

                    <Form.Label>Date of Birth</Form.Label>
                    <Form.Control type="date" value={dateOfBirth} onChange={handleChangeDateOfBirth}/>

                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>


            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddNewAccountDialog;
