import axios from 'axios';
import * as config from './config';

export const fetchAllUserAccount = () => {

    let userAccounts = [];
    axios.get(config.userAccountManagementBackendUrl)
        .then(res => {
            const accounts = res.data;
            userAccounts = res.data;
        }).catch((err) => {
            console.log(err)
    });

    return userAccounts;
};

export const createNewUserAccount = (newUserAccount) => {
    axios.post(config.userAccountManagementBackendUrl, newUserAccount)
        .then(res => {
            const account = res.data;
            console.log(account);
            return account;
        })
};

export const deleteUserAccountById = (id) => {
    axios.delete(config.userAccountManagementBackendUrl + `\\${id}`)
        .then(res => {
            const account = res.data;
            console.log(account);
            return account;
        })
};

export const updateUserAccount = (newUserAccount) => {
    axios.patch(config.userAccountManagementBackendUrl, newUserAccount)
        .then(res => {
            const account = res.data;
            console.log(account);
            return account;
        })
};
