const AddNewAccountDialog = (props) => {

    let [values, setValues] = useState({firstName: '', lastName: '', emailAddress: '', dateOfBirth: ''});

    const handleInputChange = e => {
        const {name, value} = e.target;
        setValues({...values, [name]: value})
    }

    const handleSubmit = event => {
        event.preventDefault();
        createNewUserAccount( {
            fistName: values.firstName,
            lastName: values.lastName,
            emailAddress: values.emailAddress,
            dateOfBirth: values.dateOfBirth
        });
    };

    const createNewUserAccount = (newUserAccount) => {
        console.log(newUserAccount);
        axios.post(config.userAccountManagementBackendUrl, newUserAccount)
            .then(res => {
                const account = res.data;
                console.log(account);
            }).catch(err => {

        })
    };

    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered>
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Add New User Account
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>

                <Form onSubmit={handleSubmit}>
                    <Row>
                        <Col>
                            <Form.Group controlId="firstName">
                                <Form.Label>First Name</Form.Label>
                                <Form.Control type="text" required value={values.firstName} onChange={handleInputChange}/>
                            </Form.Group>
                        </Col>
                        <Col>
                            <Form.Group controlId="lastName">
                                <Form.Label>Last Name</Form.Label>
                                <Form.Control type="text" required value={values.lastName} onChange={handleInputChange}/>
                            </Form.Group>
                        </Col>
                    </Row>

                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" required value={values.emailAddress} onChange={handleInputChange}/>
                    </Form.Group>

                    <Form.Label>Date of Birth</Form.Label>
                    <Form.Control type="date" required value={values.dateOfBirth} onChange={handleInputChange}/>

                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>


            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddNewAccountDialog;
