import React, {useState} from 'react';
import axios from 'axios';

import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from "react-bootstrap/Form";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'react-datepicker/dist/react-datepicker.css';

import * as config from '../services/config';

const AddNewAccountDialog = (props) => {

    let [values, setValues] = useState({firstName: '', lastName: '', emailAddress: '', dateOfBirth: ''});

    const handleInputChange = e => {
        const {name, value} = e.target;
        console.log(e)
        setValues({...values, [name]: value})
    };

    const handleSubmit = event => {
        event.preventDefault();
        createNewUserAccount({
            fistName: values.firstName,
            lastName: values.lastName,
            emailAddress: values.emailAddress,
            dateOfBirth: values.dateOfBirth
        });
    };

    const createNewUserAccount = (newUserAccount) => {
        console.log(newUserAccount);
        axios.post(config.userAccountManagementBackendUrl, newUserAccount)
            .then(res => {
                const account = res.data;
                console.log(account);
            }).catch(err => {

        })
    };

    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered>
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Add New User Account
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>

                <Form onSubmit={handleSubmit}>
                    <Row>
                        <Col>
                            <Form.Label>First Name</Form.Label>
                            <Form.Control type="text" name={values.firstName} defaultValue="Bob" required onChange={handleInputChange}/>
                        </Col>
                        <Col>
                            <Form.Label>Last Name</Form.Label>
                            <Form.Control type="text" required onChange={handleInputChange}/>
                        </Col>
                    </Row>

                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" required onChange={handleInputChange}/>

                    <Form.Label>Date of Birth</Form.Label>
                    <Form.Control type="date" required onChange={handleInputChange}/>

                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>


            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddNewAccountDialog;
