import React, {useState} from 'react';
import axios from 'axios';

import Modal from 'react-bootstrap/Modal'
import Button from 'react-bootstrap/Button';
import Form from "react-bootstrap/Form";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import 'react-datepicker/dist/react-datepicker.css';

import * as config from "./services/config";

const AddNewAccountDialog = (props) => {

    let [firstName, lastName, emailAddress, dateOfBirth] = useState();

    const handleChangeFistName = e => {
        firstName = e.target.value;
    };

    const handleChangeLastName = e => {
        lastName = e.target.value;
    };

    const handleChangeEmailAddress = e => {
        emailAddress = e.target.value;
    };

    const handleChangeDateOfBirth = e => {
        dateOfBirth = e.target.value;
    };

    const handleSubmit = event => {
        event.preventDefault();
        createNewUserAccount( {
            fistName: firstName,
            lastName: lastName,
            emailAddress: emailAddress,
            dateOfBirth: dateOfBirth
        });
    };

    const createNewUserAccount = (newUserAccount) => {
        console.log(newUserAccount);
        axios.post(config.userAccountManagementBackendUrl, newUserAccount)
            .then(res => {
                const account = res.data;
                console.log(account);
            }).catch(err => {

        })
    };

    return (
        <Modal
            {...props}
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered>
            <Modal.Header closeButton>
                <Modal.Title id="contained-modal-title-vcenter">
                    Add New User Account
                </Modal.Title>
            </Modal.Header>
            <Modal.Body>

                <Form onSubmit={handleSubmit}>
                    <Row>
                        <Col>
                            <Form.Control placeholder="First name" required value={firstName} onChange={handleChangeFistName}/>
                        </Col>
                        <Col>
                            <Form.Control placeholder="Last name" required value={lastName} onChange={handleChangeLastName}/>
                        </Col>
                    </Row>

                    {/*<Form.Group controlId="firstName">*/}
                    {/*<Form.Label>First Name</Form.Label>*/}
                    {/*<Form.Control type="text" required value={firstName} onChange={handleChangeFistName}/>*/}
                    {/*</Form.Group>*/}

                    {/*<Form.Group controlId="lastName">*/}
                    {/*<Form.Label>Last Name</Form.Label>*/}
                    {/*<Form.Control type="text" required value={lastName} onChange={handleChangeLastName}/>*/}
                    {/*</Form.Group>*/}

                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control type="email" required value={emailAddress} onChange={handleChangeEmailAddress}/>
                    </Form.Group>

                    <Form.Label>Date of Birth</Form.Label>
                    <Form.Control type="date" required value={dateOfBirth} onChange={handleChangeDateOfBirth}/>

                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>


            </Modal.Body>
            <Modal.Footer>
                <Button onClick={props.onHide}>Close</Button>
            </Modal.Footer>
        </Modal>
    );
};

export default AddNewAccountDialog;
