import React from 'react';
import Table from 'react-bootstrap/Table';

class UserAccountsTable extends React.Component  {
    constructor(props) {
        super(props);
        this.state = {
            userAccounts: []
        }
    }


    render(){

       function sortTableByName() {
            let sortedArray = this.props.users;
            console.log(sortedArray);
            sortedArray.sort(function (a, b){return a.firstName.localeCompare((b.firstName))} );
            this.setState({
                userAccounts: sortedArray,
            })
        };

        const userAccounts = this.props.users;
        console.log(userAccounts)
        return (
            <Table id="userAccountsTable" striped bordered hover width="100%">
                <thead>
                <tr>
                    <th class="th-sm">#</th>
                    <th class="th-sm"
                        onClick={this.sortTableByName}
                    >First Name</th>
                    <th class="th-sm">Last Name</th>
                    <th class="th-sm">Email</th>
                    <th class="th-sm">Date of Birth</th>
                </tr>
                </thead>

                <tbody>
                {userAccounts.map(account => (
                    <tr>
                        <td>{account.id}</td>
                        <td>{account.firstName}</td>
                        <td>{account.lastName}</td>
                        <td>{account.emailAddress}</td>
                        <td>{account.dateOfBirth}</td>
                    </tr>
                ))}
                </tbody>

            </Table>
        )
    }


};

export default UserAccountsTable;
