import React from 'react';
import Table from 'react-bootstrap/Table';

class UserAccountsTable extends React.Component  {
    constructor(props) {
        super(props);
        this.state = {
            userAccounts: []
        }
    }

     sortTableAscending = (userAccounts, criteria) => {
        let sortedArray = userAccounts;
        sortedArray.sort(function (a, b){return a[criteria].localeCompare(b[criteria])} );
        this.setState({
            userAccounts: sortedArray,
        })
    };

    sortTableDescending = (userAccounts, criteria) => {
        let sortedArray = userAccounts;
        sortedArray.sort(function (a, b){return b[criteria].localeCompare(a[criteria])} );
        this.setState({
            userAccounts: sortedArray,
        })
    };

    render(){
        const userAccounts = this.props.users;
        return (
            <Table id="userAccountsTable" striped bordered hover width="100%">
                <thead>
                <tr>
                    <th class="th-sm">#</th>
                    <th class="th-sm" onClick={() => this.sortTableAscending(userAccounts, "firstName")}>
                        First Name
                        <span className="glyphicon glyphicon-triangle-bottom"></span>
                    </th>
                    <th class="th-sm">Last Name</th>
                    <th class="th-sm">Email</th>
                    <th class="th-sm">Date of Birth</th>
                </tr>
                </thead>

                <tbody>
                {userAccounts.map(account => (
                    <tr>
                        <td>{account.id}</td>
                        <td>{account.firstName}</td>
                        <td>{account.lastName}</td>
                        <td>{account.emailAddress}</td>
                        <td>{account.dateOfBirth}</td>
                    </tr>
                ))}
                </tbody>

            </Table>
        )
    }


};

export default UserAccountsTable;
