import React from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button';

class UserAccountsTable extends React.Component  {
    constructor(props) {
        super(props);
        this.state = {
            userAccounts: []
        }
    }

     sortTableAscending = (userAccounts, criteria) => {
        let sortedArray = userAccounts;
        sortedArray.sort(function (a, b){return a[criteria].localeCompare(b[criteria])} );
        this.setState({
            userAccounts: sortedArray,
        })
    };

    sortTableDescending = (userAccounts, criteria) => {
        let sortedArray = userAccounts;
        sortedArray.sort(function (a, b){return b[criteria].localeCompare(a[criteria])} );
        this.setState({
            userAccounts: sortedArray,
        })
    };

    render(){
        const userAccounts = this.props.users;
        return (
            <Table id="userAccountsTable" striped bordered hover width="100%">
                <thead>
                <tr>
                    <th class="th-sm">#</th>
                    <th class="th-sm" >
                        First Name
                        <Button onClick={() => this.sortTableAscending(userAccounts, "firstName")} variant="outline-info">&#8593;</Button>{' '}
                        <Button onClick={() => this.sortTableDescending(userAccounts, "firstName")} variant="outline-info">&#8595;</Button>{' '}
                    </th>
                    <th class="th-sm">Last Name
                        <Button onClick={() => this.sortTableAscending(userAccounts, "lastName")} variant="outline-info">&#8593;</Button>{' '}
                        <Button onClick={() => this.sortTableDescending(userAccounts, "lastName")} variant="outline-info">&#8595;</Button>{' '}

                    </th>
                    <th class="th-sm">Email
                    <Button onClick={() => this.sortTableAscending(userAccounts, "emailAddress")} variant="outline-info">&#8593;</Button>{' '}
                    <Button onClick={() => this.sortTableDescending(userAccounts, "emailAddress")} variant="outline-info">&#8595;</Button>{' '}
                    </th>
                    <th class="th-sm">Date of Birth</th>
                </tr>
                </thead>

                <tbody>
                {userAccounts.map(account => (
                    <tr>
                        <td>{account.id}</td>
                        <td>{account.firstName}</td>
                        <td>{account.lastName}</td>
                        <td>{account.emailAddress}</td>
                        <td>{account.dateOfBirth}</td>
                    </tr>
                ))}
                </tbody>

            </Table>
        )
    }


};

export default UserAccountsTable;
